class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int count = 0;
        for(int i = 0;i < n / 2;i++){
            for(int j = 0;j < m / 2;j++){
                int ones = 0;
                ones += grid[i][j];
                ones += grid[i][m - j - 1];
                ones += grid[n - i - 1][j];
                ones += grid[n - i - 1][m - j - 1];

                if(ones == 1 || ones == 3){
                    count += 1;
                }else if(ones == 2){
                    count += 2;
                }

            }
        }
        int change = 0;
        int ones = 0;
        if(n % 2 == 1){
            for(int j = 0;j < m / 2;j++){
                if(grid[n/2][m - 1 - j] != grid[n/2][j]){
                    change++;
                }else if(grid[n/2][j]){
                    ones+=2;
                }
            }
            if(m % 2 == 1 && grid[n/2][m/2] == 1){
                count++;
            } 
        }
        if(m % 2 == 1){
            for(int j = 0;j < n / 2;j++){
                if(grid[n - 1 - j][m/2] != grid[j][m/2]){
                    change++;
                }else if(grid[j][m/2]){
                    ones+=2;
                }
            }
            if(n % 2 == 1 && grid[n/2][m/2] == 1){
                count++;
            } 
        }
        if(m % 2 == 1 && n % 2 == 1 && grid[n/2][m/2] == 1){
            count--;
        } 

        if(ones % 4 == 2 && change == 0){
            count += 2;
        }

        return count + change;

    }
};
