class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                vector<int>hash(2,0);
                hash[grid[i][j]]++;
                hash[grid[i][m-1-j]]++;
                hash[grid[n-1-i][j]]++;
                hash[grid[n-1-i][m-1-j]]++;
                ans+=(4-max(hash[0],hash[1]));
            }
        }
        int ct=0;
        int onect=0;
        if(n&1){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-1-i])ct++;
                else if(grid[n/2][i])onect+=2;
            }
        }
        if(m&1){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2])ct++;
                else if(grid[i][m/2])onect+=2;
            }
        }
        // if(m&1 and n&1 and grid[n/2][m/2])ct++;
        // if(onect%4==0){
        //     return ans+ct;
        // }
        // if(ct==0)return ans+2;
        // return ans+ct;
        cout<<"********"<<endl;
        if(ct>0){
            return ans+ct+((((n)&1) && ((m)&1))?grid[n/2][m/2]:0);
        }
        if(ct==0 and onect%4==0 ){
            return ans+((((n)&1) && ((m)&1))?grid[n/2][m/2]:0);
        }
        cout<<"hii"<<endl;
        cout<<ans<<endl;
        return ans+2+((((n)&1) && ((m)&1))?grid[n/2][m/2]:0);
    }
};
