class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n = grid.size();
        int m=grid[0].size();

        int res=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int zero=0,one=0;
                pair<int,int> a,b,c,d;

                a={i,j};
                b={i,m-j-1};
                c={n-i-1,j};
                d={n-i-1,m-j-1};

                if(grid[a.first][a.second]) one++;
                else zero++;

                if(grid[b.first][b.second]) one++;
                else zero++;

                if(grid[c.first][c.second]) one++;
                else zero++;

                if(grid[d.first][d.second]) one++;
                else zero++;

                if(one==0 || zero==0)
                {
                    continue;
                }

                res+=4-max(zero,one);
            }
        }

        int op=0;
        int cnt_one=0;
        if(n%2)
        {
            int i=n/2;

            for(int j=0;j<m/2;j++)
            {
                if(grid[i][j]!=grid[i][m-j-1])
                    op++;
                else if(grid[i][j])
                    cnt_one+=2;

            }

        }

        if(m%2)
        {
            int j=m/2;

            for(int i=0;i<n/2;i++)
            {
                if(grid[i][j]!=grid[n-i-1][j])
                    op++;
                else if(grid[i][j])
                    cnt_one+=2;

            }
        }


        if(cnt_one%4==0)
        {
            res+=op;
        }
        else{

            if(op==0)
            {
               res+=2; 
            }
            else{
                res+=op;
            }
        }

        if(n%2 && m%2){
            if(grid[n/2][m/2])
            res+=1;
        }

        return res;

    }
};
