class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for(int i = 0; i < n / 2; i++){
            for(int j = 0; j < m / 2; j++){
                int a = grid[i][j];
                int b = grid[i][m - j - 1];
                int c = grid[n - i - 1][j];
                int d = grid[n - i - 1][m - j - 1];
                int o = 0;
                if(a == 0)o++;
                if(b == 0)o++;
                if(c == 0)o++;
                if(d == 0)o++;
                ans += min(o, 4 - o);
            }
        }
        int c = 0, ans2 = 0;
        if(n % 2 == 1){
            for(int i = 0; i < m / 2; i++){
                if(grid[n / 2][i] != grid[n / 2][m - i - 1]){
                    ans2++;
                }
                if(grid[n / 2][i] == 1 && grid[n / 2][i] == grid[n / 2][m - i - 1]){
                    c++;
                }
            }
        }
        if(m % 2 == 1){
            for(int i = 0; i < n / 2; i++){
                if(grid[i][m / 2] != grid[n - i - 1][m / 2]){
                    ans2++;
                }
                if(grid[i][m / 2] == 1 && grid[i][m / 2] == grid[n - i - 1][m / 2]){
                    c++;
                }
            }
        }
        if(n % 2 == 1 && m % 2 == 1 && grid[n / 2][m / 2] == 1)ans++;
        if(ans2 == 0 && c % 2 == 1)ans += 2;
        ans += ans2;
        return ans;
    }
};
