class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),c1=0,c2=0,ans=0;
        for(int i=0;i<n/2;++i)
        {
            for(int j=0;j<m/2;++j)
            {
                int sum=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+ grid[n-i-1][m-j-1];
                ans+=min(sum,4-sum);
            }
        }
        if(m&1)
        {
            for(int i=0;i<n/2;++i)
            {
                // For odd 1s
                if(grid[i][m/2]!=grid[n-i-1][m/2])
                    ++c1;
                // for even 1s
                if(grid[i][m/2]==grid[n-i-1][m/2] && grid[i][m/2]==1)
                    c2+=grid[i][m/2];
            }
        }
        if(n&1)
        {
            for(int i=0;i<m/2;++i)
            {
                if(grid[n/2][i]!=grid[n/2][m-i-1])
                    ++c1;
                if(grid[n/2][i]==grid[n/2][m-i-1] && grid[n/2][i]==1)
                    c2+=grid[n/2][i];
            }
        }
        if((c2&1) && (!c1))
            ans+=2;
        if((n&1) && (m&1))
            ans+=grid[n/2][m/2];
        return ans+c1;
    }
};
