#define ll long long
#define REP(i,n)        FOR(i,0,n)
#define SORT(v)         sort((v).begin(),(v).end())
#define FOR(i,a,b)      for(ll i=(a);i<(b);i++)

class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int total = 0;
        REP(i, n/2) REP(j, m/2) {
            int sum = grid[i][j] + grid[i][m-1-j] + grid[n-1-i][j] + grid[n-1-i][m-1-j];
            total += min(4-sum, sum);
        }
        int diff = 0;
        int same = 0;
        if (n%2 == 1 && m%2 == 1) total += grid[n/2][m/2];
        if (n%2 == 1) REP(j, m/2) {
            if (grid[n/2][j] != grid[n/2][m-1-j]) total++;
            grid[n/2][j] == grid[n/2][m-1-j] ? same += grid[n/2][j]*2 : diff++;
        }
        if (m%2 == 1) REP(i, n/2) {
            if (grid[i][m/2] != grid[n-1-i][m/2]) total++;
            grid[i][m/2] == grid[n-1-i][m/2] ? same += grid[i][m/2]*2 : diff++;
        }
        if (same%4 == 2 && diff == 0) {
            total += 2;
        }

        return total;
    }
};
