class Solution:
    def minFlips(self, G: List[List[int]]) -> int:
        N,M = len(G), len(G[0])
        Ans = 0
        if N%2 == 1 and M%2 == 1 and G[N//2][M//2] == 1:
            Ans += 1
        S = [0]*3
        def PL(M,L,is_row=True):
            A,B = 0, L - 1
            while A < B:
                sM = G[M][A]+G[M][B] if is_row else G[A][M]+G[B][M]
                S[sM] += 1
                A += 1
                B -= 1
        if N%2 == 1:
            PL(N//2,M)
        if M%2 == 1:
            PL(M//2,N, is_row=False)
        if S[2]%2 == 1:
            Ans += S[1] if S[1]>0 else 2
        else:
            Ans += S[1]
        for i in range(N//2):
            for j in range(M//2):
                SM = G[i][j]+G[N-1-i][j]+G[i][M-1-j]+G[N-1-i][M-1-j]
                if SM not in {0,4}:
                    Ans += min(SM,4-SM)
        return Ans
