class Solution { 
public: 
    int minFlips(vector<vector<int>>& g) { 
        int n = g.size(), m = g[0].size(), res = 0;

        if (n % 2 && m % 2 && g[n / 2][m / 2] == 1) res++;

        vector<int> sh(3, 0);
        if (n % 2) {
            for (int a = 0, b = m - 1; a < b; a++, b--) {
                sh[g[n / 2][a] + g[n / 2][b]]++;
            }
        }

        if (m % 2) {
            for (int a = 0, b = n - 1; a < b; a++, b--) {
                sh[g[a][m / 2] + g[b][m / 2]]++;
            }
        }

        res += sh[2] % 2 ? sh[1] ? sh[1] : 2 : sh[1];

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = g[i][j], b = g[n - 1 - i][j], c =g[i][m - 1 - j], d = g[n - 1 - i][m - 1 - j];
                int s = a + b + c + d;
                if (s && s != 4) res += min(s, 4 - s);
            }
        }

        return res;

    } 
};
