class Solution
{
    public int minFlips(int[][] a) 
    {
        int n = a.length, m = a[0].length;
        int ans = 0;
        if (n % 2 == 1 && m % 2 == 1) 
            if (a[n / 2][m / 2] == 1)
                ans += 1;
        int[] b = new int[3];
        if (n % 2 == 1)
        {
            int l = 0, r = m - 1;
            while (l < r) 
            {
                int sm = a[n / 2][l] + a[n / 2][r];
                b[sm] += 1;
                l += 1;
                r -= 1;
            }
        }
        if (m % 2 == 1)
        {
            int l = 0, r = n - 1;
            while (l < r)
            {
                int sm = a[l][m / 2] + a[r][m / 2];
                b[sm] += 1;
                l += 1;
                r -= 1;
            }
        }
        if (b[2] % 2 == 1) 
            ans+=(b[1]>0)?b[1]:2;     
        else
            ans += b[1];
        for (int i = 0; i < n / 2; ++i) 
        {
            for (int j = 0; j < m / 2; ++j) 
            {
                int sm = a[i][j] + a[n-1-i][j] + a[i][m-1-j] + a[n-1-i][m-1-j];
                if (sm != 0 && sm != 4)
                    ans += Math.min(sm, 4 - sm);
            }
        }
        return ans;
    }
}
