class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        vector<int> shift(3, 0);
        if (n % 2 == 1) {
            int x = 0, y = m - 1;
            while (x< y) {
                int sm = grid[n / 2][x] + grid[n / 2][y];
                shift[sm] += 1;
                x += 1;
                y -= 1;
            }
        }

        if (m % 2 == 1) {
            int p = 0, q = n - 1;
            while (p < q) {
                int sm = grid[p][m / 2] + grid[q][m / 2];
                shift[sm] += 1;
                p += 1;
                q -= 1;
            }
        }

        if (shift[2] % 2 == 1) {
            if (shift[1] > 0) {
                ans += shift[1];
            } else {
                ans += 2;
            }
        } else {
            ans += shift[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int p = grid[i][j];
                int q = grid[n - 1 - i][j];
                int r = grid[i][m - 1 - j];
                int s = grid[n - 1 - i][m - 1 - j];

                int sm =p + q + r + s;

                if (sm != 0 && sm != 4) {
                    ans += min(sm, 4 - sm);
                }
            }
        }

        return ans;
    }

};
