#include <vector>
#include <numeric>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int r = g.size();
        int c = g[0].size();

        int ans = 0;

        for (int i = 0; i < r / 2; ++i)
        {
            for (int j = 0; j < c / 2; ++j)
            {
                vector<int> el = {
                    g[i][j],
                    g[r - i - 1][j],
                    g[i][c - j - 1],
                    g[r - i - 1][c - j - 1]
                };

                int ones = accumulate(el.begin(), el.end(), 0);

                ans += min(ones, 4 - ones);
            }
        }

        int pos = 0;
        int cnt = 0;

        long long rem = 0;
        int n = r;

        for(int i = 0; i < n; i++)
        {
            rem += i;
        }


        if (r % 2 != 0)
        {
            for (int j = 0; j < c / 2; ++j) 
            {
                if (g[r / 2][j] != g[r / 2][c - j - 1])
                {
                    ans += 1;
                    pos += 1;
                }
                else
                {
                    if (g[r / 2][j] == 1)
                    {
                        cnt += 1;
                    }
                }
            }
        }

        if (c % 2 != 0)
        {
            for (int i = 0; i < r / 2; ++i)
            {
                if (g[i][c / 2] != g[r - i - 1][c / 2])
                {
                    ans += 1;
                    pos += 1;
                } 
                else
                {
                    if (g[i][c / 2] == 1)
                    {
                        cnt += 1;
                    }
                }
            }
        }

        if (r % 2 != 0 && c % 2 != 0 && g[r / 2][c / 2] == 1) {
            ans += 1;
        }

        if (pos == 0 && cnt % 2 != 0) {
            ans += 2;
        }

        return ans;
    }
};
