class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int flips = 0, n=grid.size(), m=grid[0].size();
        for(int i=0,j=n-1; i<j; i++,j--){
            for(int a=0,b=m-1; a<b; a++,b--){
                int one = 0, zero = 0;
                if(grid[i][a]==1) one++;
                else zero++;
                if(grid[i][b]==1) one++;
                else zero++;
                if(grid[j][a]==1) one++;
                else zero++;
                if(grid[j][b]==1) one++;
                else zero++;
                flips += min(one,zero);
            }
        }
        if(n%2==1 && m%2==1){
            int nmid=n/2, mmid=m/2;
            int to_be_flipped = 0, pair_of_one = 0;
            for(int i=0,j=m-1;i<j;i++,j--){
                if(grid[nmid][i]==1 && grid[nmid][j]==1)
                    pair_of_one++;
                else if(grid[nmid][i]==1 || grid[nmid][j]==1)
                    to_be_flipped++;
            }
            for(int i=0,j=n-1;i<j;i++,j--){
                if(grid[i][mmid]==1 && grid[j][mmid]==1)
                    pair_of_one++;
                else if(grid[i][mmid]==1 || grid[j][mmid]==1)
                    to_be_flipped++;
            }
            pair_of_one %= 2;
            if(pair_of_one+to_be_flipped>=2)
                flips += to_be_flipped;
            else
                flips += to_be_flipped+(pair_of_one*2);

            if(grid[nmid][mmid]==1)
                flips++;
        }
        else if(n%2==1 || m%2==1){
            if(n%2==1){
                int nmid=n/2;
                int to_be_flipped = 0, pair_of_one = 0;
                for(int i=0,j=m-1;i<j;i++,j--){
                    if(grid[nmid][i]==1 && grid[nmid][j]==1)
                        pair_of_one++;
                    else if(grid[nmid][i]==1 || grid[nmid][j]==1)
                        to_be_flipped++;
                }
                pair_of_one%=2;
                if(pair_of_one+to_be_flipped>=2)
                    flips += to_be_flipped;
                else
                    flips += to_be_flipped+(pair_of_one*2);
            }
            else{
                int mmid=m/2;
                int to_be_flipped = 0, pair_of_one = 0;
                for(int i=0,j=n-1;i<j;i++,j--){
                    if(grid[i][mmid]==1 && grid[j][mmid]==1)
                        pair_of_one++;
                    else if(grid[i][mmid]==1 || grid[j][mmid]==1)
                        to_be_flipped++;
                }
                pair_of_one%=2;
                if(pair_of_one+to_be_flipped>=2)
                    flips += to_be_flipped;
                else
                    flips += to_be_flipped+(pair_of_one*2);
            }
        }
        return flips;
    }
};
