class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
            for(int i=0;i<n/2;i++){
                for(int j=0;j<m/2;j++){
                    int n1=0;
                    if(grid[i][j]==1)n1++;
                    if(grid[n-1-i][j]==1)n1++;
                    if(grid[i][m-1-j]==1)n1++;
                    if(grid[n-1-i][m-1-j])n1++;
                    ans+=min(n1,4-n1);                    
                }
            }
        if(n%2!=0&&m%2!=0){
            if(grid[n/2][m/2]==1)ans++;
        }
        int n01=0,n1=0;
        if(n%2!=0){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-1-i]){
                    n01++;
                }else{
                    if(grid[n/2][i]==1)n1++;
                }
            }
        }
        if(m%2!=0){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]){
                    n01++;
                }else{
                    if(grid[i][m/2]==1)n1++;
                }
            }
        }
        if(n01>=1){
            ans+=n01;
        }else{
            if(n1%2!=0){
                ans+=2;
            }
        }
        return ans;
    }
};
