class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n=len(grid);m=len(grid[0]);ans=0;k=0
        for i in range(n//2):
            for j in range(m//2):
                d={};d[0]=0;d[1]=0
                d[grid[i][j]]+=1
                d[grid[n-i-1][j]]+=1
                d[grid[i][m-j-1]]+=1
                d[grid[n-i-1][m-j-1]]+=1
                ans+=min(d[0],d[1])
        c1=c2=0
        if m%2==1:
            for i in range(n//2):
                if grid[i][m//2]!=grid[n-i-1][m//2]:c1+=1
                elif grid[i][m//2]==1:c2+=1
        if n%2==1:
            for j in range(m//2):
                if grid[n//2][j]!=grid[n//2][m-j-1]:c1+=1
                elif grid[n//2][j]==1:c2+=1
        ans+=c1
        if c1==0 and c2%2!=0:ans+=2
        if n%2==1 and m%2==1 and grid[n//2][m//2]==1:ans+=1
        return ans
