class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int row=grid.size(),col=grid[0].size();
        int ans=0;
        if(row%2==1 && col%2==1){
            if(grid[row/2][col/2]==1){
                ans++;
            }
        }
        vector<int>s(3,0);
        if(row%2==1){
            int at=0,bt=col-1;
            while(at<bt){
                int val=grid[row/2][at]+grid[row/2][bt];
                s[val]++;
                at++;
                bt--;
            }
        }
        if(col%2==1){
            int at=0,bt=row-1;
            while(at<bt){
                int val=grid[at][col/2]+grid[bt][col/2];
                s[val]++;
                at++;
                bt--;
            }
        }
        if(s[2]%2==1){
            if(s[1]>0){
                ans+=s[1];
            }else{
                ans+=2;
            }
        }else{
            ans+=s[1];
        }
        for(int i=0;i<row/2;i++){
            for(int j=0;j<col/2;j++){
                int at=grid[i][j];
                int bt=grid[row-1-i][j];
                int ct=grid[i][col-1-j];
                int dt=grid[row-1-i][col-1-j];
                int val=at+bt+ct+dt;
                if(val!=0 && val!=4){
                    ans+=min(val,4-val);
                }
            }
        }
        return ans;

    }
};
