public class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int answerwa = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                answerwa += 1;
            }
        }

        int[] shiitiz = new int[3];
        if (n % 2 == 1) {
            int o = 0, b = m - 1;
            while (o < b) {
                int sm = grid[n / 2][o] + grid[n / 2][b];
                shiitiz[sm] += 1;
                o += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int o = 0, b = n - 1;
            while (o < b) {
                int sm = grid[o][m / 2] + grid[b][m / 2];
                shiitiz[sm] += 1;
                o += 1;
                b -= 1;
            }
        }

        if (shiitiz[2] % 2 == 1) {
            if (shiitiz[1] > 0) {
                answerwa += shiitiz[1];
            } else {
                answerwa += 2;
            }
        } else {
            answerwa += shiitiz[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int o = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int sm = o + b + c + d;

                if (sm != 0 && sm != 4) {
                    answerwa += Math.min(sm, 4 - sm);
                }
            }
        }

        return answerwa;
    }
}
