class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int out=0;int dd=0;int pp=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(i==m-1-i&&j==n-1-j){
                    out+=1*grid[i][j];grid[i][j]=0;
                }
                else  if(i==m-1-i){
                  int  cnt=grid[i][j]+grid[i][n-1-j];
                    if(cnt%2){dd++;grid[i][j]=grid[i][n-1-j];}
                    else{pp+=grid[i][j];}
                    out+=(cnt%2);
                }
                else  if(j==n-1-j){
                  int  cnt=grid[i][j]+grid[m-1-i][j];
                    if(cnt%2){dd++;grid[i][j]=grid[m-1-i][j];}
                    else{pp+=grid[i][j];}

                    out+=1*(cnt%2);
                }

                else  {
                  int  cnt=grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j];
                    if(cnt<=2){out+=cnt;
                               grid[i][j]=0;
                                                              grid[m-1-i][j]=0;
                                                              grid[i][n-1-j]=0;
                                                              grid[m-1-i][n-1-j]=0;



                              }
                    else{out+=4-cnt;
                                                       grid[i][j]=1;
                                                              grid[m-1-i][j]=1;
                                                              grid[i][n-1-j]=1;
                                                              grid[m-1-i][n-1-j]=1;


                        }
                }
            }
}
        pp/=2;

        if(pp%2&&dd==0){out+=2;}
        return out;
    }
};
