class Solution {
public:
    int minFlips(vector<vector<int>>& grid) 
    {
        int double1=0;
        int zopair=0;
        int single1=0;
        int ans=0;
        int n=grid.size();
        int m=grid[0].size();
        int val1,val2;
        if(grid.size()%2==0)
        {
            val1=grid.size()/2;
        }
        else
        {
            val1=grid.size()/2+1;
        }
        if(grid[0].size()%2==0)
        {
            val2=grid[0].size()/2;
        }
        else
        {
            val2=grid[0].size()/2+1;
        }
        for(int i=0;i<val1;i++)
        {
            for(int j=0;j<val2;j++)
            {
                int count0=0,count1=0;
                if(grid[i][j]==0)
                {
                    count0++;
                }
                else
                {
                    count1++;
                }
                if(i!=n-i-1)
                {
                    if(grid[n-i-1][j]==0)
                    count0++;
                    else
                    count1++;
                }
                if(j!=m-j-1)
                {
                    if(grid[i][m-j-1]==0)
                    count0++;
                    else
                    count1++;
                }
                if(i!=n-i-1 && j!=m-j-1)
                {
                    if(grid[n-i-1][m-j-1]==0)
                    count0++;
                    else
                    count1++;
                }
                ans+=min(count0,count1);
                if((count1+count0)==1 && grid[i][j]==1)
                {
                    ans++;
                }
                else if((count1+count0)==2)
                {
                    if(count1==2)
                    {
                        double1+=2;
                    }
                    else if(count1==1 && count0==1)
                    {
                        zopair+=2;
                    }
                }
            }
        }
        if(double1%4==2)
        {
            if(zopair==0)
            {
                ans+=2;
            }
        }
        return ans;
    }
};
