typedef long long int ll;


class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {

        vector<ll> curr( 4, 1e6);
        curr[0] = 0;

        ll n = arr.size();
        ll m = arr[0].size();

        for( ll ci=0, cj=m-1; ci<=cj; ci++, cj--) {
            for( ll ri=0, rj=n-1; ri<=rj; ri++, rj--) {
                pair<ll,ll> p0;
                pair<ll,ll> p1;
                if((ci==cj) && (ri==rj)) {
                    p0 = { 1, (arr[ri][ci]%2!=0)};
                    p1 = { 1, (arr[ri][ci]%2!=1)};
                }  else if(ci==cj) {
                    p0 = { 2, (arr[ri][ci]%2!=0)+(arr[rj][ci]%2!=0)};
                    p1 = { 2, (arr[ri][ci]%2!=1)+(arr[rj][ci]%2!=1)};
                } else if(ri==rj) {
                    p0 = { 2, (arr[ri][ci]%2!=0)+(arr[ri][cj]%2!=0)};
                    p1 = { 2, (arr[ri][ci]%2!=1)+(arr[ri][cj]%2!=1)};
                } else {
                    p0 = { 4, (arr[ri][ci]%2!=0)+(arr[ri][cj]%2!=0)+(arr[rj][ci]%2!=0)+(arr[rj][cj]%2!=0)};
                    p1 = { 4, (arr[ri][ci]%2!=1)+(arr[ri][cj]%2!=1)+(arr[rj][ci]%2!=1)+(arr[rj][cj]%2!=1)};
                }
                vector<ll> nxt( 4, 1e6);
                for( int id=0; id<4; id++) {
                    nxt[id] = min( nxt[id], curr[id]+p0.second);
                    nxt[(id+p1.first)%4] = min( nxt[(id+p1.first)%4], curr[id]+p1.second);
                }
                swap( curr, nxt);
            }
        }

        return curr[0];

    }
};
