class Solution { 
public: 
    int minFlips(vector<vector<int>>& grid) { 
        int n = grid.size(), m = grid[0].size(); 
        int answer = 0;

        if (n % 2 == 1 && m % 2 == 1) { 
            if (grid[n / 2][m / 2] == 1) { 
                answer += 1;
            } 
        } 

        vector<int> shift(3, 0); 
        if (n % 2 == 1) { 
            int a = 0, b = m - 1; 
            while (a < b) { 
                int t = grid[n / 2][a] + grid[n / 2][b];
                shift[t] += 1;
                a += 1; 
                b -= 1; 
            } 
        } 

        if (m % 2 == 1) { 
            int a = 0, b = n - 1; 
            while (a < b) { 
                int t = grid[a][m / 2] + grid[b][m / 2];
                shift[t] += 1;
                a += 1; 
                b -= 1; 
            } 
        } 

        if (shift[2] % 2 == 1) { 
            if (shift[1] > 0) { 
                answer += shift[1];
            } else { 
                answer += 2;
            } 
        } else { 
            answer += shift[1];
        } 

        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int t = grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j];
                if (t != 0 && t != 4) {
                    answer += min(t, 4 - t);
                } 
            } 
        } 

        return answer;
    } 
};
