class Solution {
public:
    int solve(int i,vector<pair<int,int>> &vec,int total,vector<vector<int>>& dp){
        int n = vec.size();
        if(i==n){
            if(total%4==0)return 0;
            return 1e9;
        }
        if (dp[i][total % 4] != -1) {
            return dp[i][total % 4];
        }
        //all 1
        int ele = vec[i].first + solve(i+1,vec,total+vec[i].second,dp);
        //all 0
        int ele2 = vec[i].second-vec[i].first + solve(i+1,vec,total,dp);
        return dp[i][total % 4] = min(ele, ele2);
    }
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        // vector<vector<int>> vis(n,vector<int>(m,0));
        vector<pair<int,int>> val;
        int N = ceil(double(n)/2);
        int M = ceil(double(m)/2);
        // cout<<N<<M<<endl;
        for(int i=0;i<N;i++){
            for(int j=0;j<M;j++){
                int ans = 0;
                int total = 4;  
                int id1 = grid[i][j];
                int id2 = grid[n-i-1][j];
                int id3 = grid[i][m-j-1];
                int id4 = grid[n-i-1][m-j-1];
                if(id1==0)ans++;
                if(id2==0)ans++;
                if(id3==0)ans++;
                if(id4==0)ans++;
                if(n-2*i==1 && m-2*j==1){
                    if(ans!=0)ans=1;
                    total=1;
                }
                else if((n-2*i==1) || (m-2*j==1)){
                    total=2;
                    ans/=2;
                }
                val.push_back({ans,total});
            }
        }
        int a1 = val.size();
        // for(int i=0;i<a1;i++){
        //     // cout<<val[i].first<<" "<<val[i].second<<endl;
        // }
        vector<vector<int>> dp(a1, vector<int>(4, -1));
        return solve(0, val, 0, dp);
    }
};
