class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ro = grid.size();
        int co = grid[0].size();
        int flips = 0;
        int rb = 0;
        int re = ro-1;
        while(rb<re){
            int cb = 0;
            int ce = co -1;
            while(cb<ce){
                int c1 = grid[rb][cb] + grid[rb][ce] + grid[re][cb] +grid[re][ce];
                int c0  = 4-c1;
                flips += min(c1,c0);
                cb ++ ;
                ce--;
            }
            rb ++ ;
            re -- ;
        }
        int po = 0 ;
        int npo = 0;
        if(ro%2){
            int t = ro/2;
            int i = 0;
            int j = co-1;
            while(i<j){
                if(grid[t][i]!=grid[t][j]){
                    npo ++ ;
                }
                else {
                    po += grid[t][i] + grid[t][j];
                }
                i++;
                j--;
            }
        }
        if(co%2){
            int t = co/2;
            int i = 0;
            int j = ro-1;
            while(i<j){
                if(grid[i][t]!=grid[j][t]){
                    npo ++ ;
                }
                else {
                    po += grid[i][t] + grid[j][t];
                }
                i++;j--;
            }
        }
        flips+=npo;
        if(po%4){
            if(npo==0){
                flips+= 2;
            }
        }
        if(co%2 && ro %2){
            flips  +=  grid[ro/2][co/2];
        }
        return flips;
    }
};
