class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for (int i1 = 0, i2 = n - 1; i1 < i2; i1++, i2--) {
          for (int j1 = 0, j2 = m - 1; j1 < j2; j1++, j2--) {
            int sum = grid[i1][j1] + grid[i1][j2] + grid[i2][j1] + grid[i2][j2];
            sum = min(sum, 4 - sum);
            ans += sum;
          }
        }
        // cout << ans << "\n";
        // centre col
        int sum = 0;
        int diff = 0;
        if (m & 1) {
          int col = m / 2;
          for (int i1 = 0, i2 = n - 1; i1 < i2; i1++, i2--) {
            if (grid[i1][col] != grid[i2][col]) {
              diff++;
            } else if (grid[i1][col] == 1) {
              sum += 2;
            }
          }

        }
        // cout << ans << "\n";
        // centre row
        if (n & 1) {
          int row = n / 2;
          for (int j1 = 0, j2 = m - 1; j1 < j2; j1++, j2--) {
            if (grid[row][j1] != grid[row][j2]) {
              diff++;
            } else if (grid[row][j1] == 1) {
              sum += 2;
            }
          }
        }
        if (diff) {
          ans += diff;
        } else {
          if (sum % 4 != 0) {
            ans += 2;
          }
        }
        if ((m & 1) and (n & 1)) {
          ans += grid[n / 2][m / 2];
        }
        return ans;
    }
};
