class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size() , m = grid[0].size();
        int ans = 0;
        for(int i = 0 ; i < n / 2 ; i++){
            for(int j = 0 ; j < m / 2 ; j++){
                map<int,int>mp;
                mp[grid[i][j]]++;
                mp[grid[i][m-1-j]]++;
                mp[grid[n-1-i][j]]++;
                mp[grid[n-1-i][m-1-j]]++;
                ans += min(mp[0],mp[1]);
            }   
        }
        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1) ans++;
        }
        if(n%2==1 && m%2==0){
            vector<int>v = grid[n/2];
                int cnt = 0;
                int i = 0 , j = m - 1;
                int unequal = 0;
                while(i<j){
                    if(v[i]==v[j]){
                        if(v[i]==1) cnt+=2;
                        i++;
                        j--;
                    }
                    else{
                        ans++;
                        unequal++;
                        i++;
                        j--;
                }
            }
            if(cnt%4!=0){
                if(unequal==0) ans += 2;
            }
        }
        if(m%2==1 && n%2==0){
            vector<int>v;
            for(int i = 0 ; i < n ; i++){
                v.push_back(grid[i][m/2]);
            }
            int cnt = 0;
            int i = 0 , j = n - 1;
            int unequal = 0;
            while(i<j){
                if(v[i]==v[j]){
                        if(v[i]==1) cnt+=2;
                        i++;
                        j--;
                    }
                    else{
                        ans++;
                        unequal++;
                        i++;
                        j--;
                }
            }
            if(cnt%4!=0){
                if(unequal==0) ans += 2;
            }
        }
        if(n%2 && m%2){
            vector<int>v = grid[n/2];
                int cnt = 0;
                int i = 0 , j = m - 1;
                int unequal = 0;
                while(i<j){
                    if(v[i]==v[j]){
                        if(v[i]==1) cnt+=2;
                        i++;
                        j--;
                    }
                    else{
                        ans++;
                        unequal++;
                        i++;
                        j--;
                }
            }
            v.clear();
            for(int i = 0 ; i < n ; i++){
                v.push_back(grid[i][m/2]);
            }
            i = 0 , j = n - 1;
            while(i<j){
                if(v[i]==v[j]){
                        if(v[i]==1) cnt+=2;
                        i++;
                        j--;
                    }
                    else{
                        ans++;
                        unequal++;
                        i++;
                        j--;
                }
            }
            if(cnt%4!=0){
                if(unequal==0) ans += 2;
            }

        }
        return ans;
    }
};
