class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n=grid.size();
        int m=grid[0].size();
        int result=0;
        for(int i=0;i<n;i++)
        {
            if(i==(n/2) && n%2)
                continue;

            for(int j=0;j<m;j++)
            {
                if(j==(m/2) && (m%2))
                    continue;

                int currSum=grid[i][j]+grid[n-1-i][j]+grid[i][m-1-j]+grid[n-1-i][m-1-j];

                if(currSum<2 && grid[i][j]==1)
                {
                    grid[i][j]=0;
                    result++;
                    continue;
                }

                if(currSum>=2 && grid[i][j]==0)
                {
                    grid[i][j]=1;
                    result++;
                    continue;
                }
            }
        }

        int ones=0;
        int chng=0;

        for(int j=0;j<(m/2) && (n%2);j++)
        {
            if(grid[n/2][j]!=grid[n/2][m-1-j])
            {
                chng++;
            }

            if(grid[n/2][j]==1)
                ones++;
        }

        for(int i=0;i<(n/2) && (m%2);i++)
        {
            if(grid[i][m/2]!=grid[n-1-i][m/2])
            {
                chng++;
            }

            if(grid[i][m/2]==1)
                ones++;
        }

        if(grid[n/2][m/2]==1 && n%2 && m%2)
            result++;

        cout<<chng<<endl;

        if(ones%2 && chng==0 && (n%2 || m%2))
            result+=2;
        else
            result+=chng;
        return result;
    }
};
