class Solution {
int check(vector<int> &s){

    int n = s.size();
    int ans= 0;
    for(int i=0; i<n/2; i++){
        if(s[i] != s[n-1-i]){
            ans++;
        }
    }
    // cout << ans << '\n';
    return ans;
}
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        bool row = false;
        bool col = false;
        if(n%2){
            row = true;
        }
        if(m%2){
            col = true;
        }

        int ans  = 0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                vector<int> vc(4);
                vc[0] = grid[i][j];
                vc[1] = grid[i][m-1-j];
                vc[2] = grid[n-1-i][j];
                vc[3] = grid[n-1-i][m-1-j];
                sort(vc.begin(), vc.end());
                if(vc[0] == vc[3]){
                    continue;
                }
                else if(vc[0] == vc[2]){
                    ans++;
                }
                else if(vc[0] == vc[1]){
                    ans += 2;
                }
                else{
                    ans += 1;
                }            
            }
        }
        // cout << ans << '\n';
        int tmp1=0, tmp2=0;
         int cnt = 0;

        if(row && col){
            if(grid[n/2][m/2] == 1){
                grid[n/2][m/2] = 0;
                ans++;
            }
        }
        if(row){
            for(int i=0; i<m; i++){
                if(grid[n/2][i] == 1){
                    cnt++;
                }


            }
            for(int k=0; k<m/2; k++){
                    if(grid[n/2][k] != grid[n/2][m-1-k]){
                        tmp1++;
                    }
                }
        }
        if(col){
            for(int i=0; i<n; i++){
                if(grid[i][m/2] == 1){
                    cnt++;
                }
            }
            for(int k=0; k<n/2; k++){
            if(grid[k][m/2] != grid[n-1-k][m/2]){
                tmp2++;
            }
        }
    }

        // cout << cnt << " " << tmp1 << " " << tmp2 <<"q\n";
        if(cnt==1){
            ans++;
        }
        else if(cnt%4==2 && tmp1+tmp2<2){ 
            ans += 2;
        }
        else{
            ans += tmp1+tmp2;
        }


        return ans;

    }
};
