class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int cnt = 0;
        vector<int>one;
        vector<int>two_zero;

        int nn = -1;
        int mm = -1;

        if(n&1){
            nn = n/2 + 1;
        }
        else nn = n/2;

        if(m&1){
            mm = m/2 + 1;
        }
        else mm = m/2;
        // cout<<nn<<' '<<mm<<'\n';

        for(int i=0;i<nn;i++){
            for(int j=0;j<mm;j++){

                if(i == n-(i+1) && j == m-(j+1)){
                    if(grid[i][j] == 1) cnt++; 
                }
                else if(i == n-(i+1)){
                    if(grid[i][j] != grid[i][m-(j+1)])
                    one.push_back(1);
                    else if(grid[i][j] == 1)
                    two_zero.push_back(1);
                }
                else if(j == m-(j+1)){
                    if(grid[i][j] != grid[n-(i+1)][j])
                    one.push_back(1);
                    else if(grid[i][j] == 1)
                    two_zero.push_back(1);
                }
                else{
                    int cnt0 = 0;
                    int cnt1 = 0;

                    if(grid[i][j] == 0) cnt0++;
                    else cnt1++;

                    if(grid[i][m-(j+1)] == 0) cnt0++;
                    else cnt1++;

                    if(grid[n-(i+1)][j] == 0) cnt0++;
                    else cnt1++;

                    if(grid[n-(i+1)][m-(j+1)] == 0) cnt0++;
                    else cnt1++;

                    cnt += 4 - max(cnt0,cnt1);
                }
            }
        }

        int sz = one.size();
        int sz1 = two_zero.size();

        if((sz+sz1)%2 == 0){
            return cnt+sz;
        }
        else{
            if(sz == 1){
                return cnt+1;
            }
            else{
                if(sz == 0){
                    return cnt+2;
                }
                else{
                    return cnt+sz;
                }
            }
        }
    }
};
