class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int a = 0;
        if (n % 2 == 1 && m % 2 == 1) {
            if (g[n / 2][m / 2] == 1) {
                a += 1;
            }
        }
        vector<int> s(3, 0);
        if (n % 2 == 1) {
            int l = 0, r = m - 1;
            while (l < r) {
                int sm = g[n / 2][l] + g[n / 2][r];
                s[sm] += 1;
                l += 1;
                r -= 1;
            }
        }
        if (m % 2 == 1) {
            int l = 0, r = n - 1;
            while (l < r) {
                int sm = g[l][m / 2] + g[r][m / 2];
                s[sm] += 1;
                l += 1;
                r -= 1;
            }
        }
        if (s[2] % 2 == 1) {
            if (s[1] > 0) {
                a += s[1];
            } else {
                a += 2;
            }
        } else {
            a += s[1];
        }
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int x = g[i][j];
                int y = g[n - 1 - i][j];
                int z = g[i][m - 1 - j];
                int w = g[n - 1 - i][m - 1 - j];

                int sm = x + y + z + w;

                if (sm != 0 && sm != 4) {
                    a += min(sm, 4 - sm);
                }
            }
        }
        return a;
    }
};
