class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        // if(m*n < 4){
        //     vector<int>c(2,0);
        //     for(int i=0;i<m;i++){
        //         for(int j=0;j<n;j++){
        //             c[grid[i][j]]++;
        //         }
        //     }
        //     return c[1];
        // }
        int ans=0,ones=0,dups=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                vector<int>c(2,0);
                c[grid[i][j]]++;
                c[grid[m-i-1][j]]++;
                c[grid[i][n-j-1]]++;
                c[grid[m-i-1][n-j-1]]++;
                ans += min(c[0],c[1]);              
            }
        }
        if(m%2){
            for(int i=0;i<n/2;i++) 
                if(grid[m/2][i]!=grid[m/2][n-i-1]) {
                    ans++;
                    ones++;
                }
            else if(grid[m/2][i]==1) dups += 2;
        }
        if(n%2){
            for(int i=0;i<m/2;i++) 
                if(grid[i][n/2]!=grid[m-i-1][n/2]) {
                    ans++;
                    ones++;
                }
            else if(grid[i][n/2]==1) dups += 2;
        }

        if(m%2 && n%2) 
            if(grid[m/2][n/2]) ans++;

        if(dups%4==2){
            if(ones>0) return ans;
            return ans+2;
        }
        return ans;


    }
};
