class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int extra=0,count=0;
        bool f=0;
        for(int i=0;i<(n+1)/2;i++){
            for(int j=0;j<(m+1)/2;j++){
                if(i==n/2 and n%2 and j==m/2 and m%2){
                    count+=(grid[i][j]==1);
                    continue;
                }
                if(i==n/2 and n%2){
                    extra+=(grid[i][j]==1 and grid[i][m-1-j]==1)?2:0;
                    count+=(grid[i][j]!=grid[i][m-1-j]);
                    if(!f){
                        f=(grid[i][j]!=grid[i][m-1-j]);
                    }
                }
                else if(j==m/2 and m%2){
                    extra+=(grid[i][j]==1 and grid[n-1-i][j]==1)?2:0;
                    count+=(grid[i][j]!=grid[n-1-i][j]);
                    if(!f){
                        f=(grid[i][j]!=grid[n-1-i][j]);
                    }
                }
                else{
                    int temp=0;
                    temp+=(grid[i][j]==1);
                    temp+=(grid[n-1-i][j]==1);
                    temp+=(grid[i][m-1-j]==1);
                    temp+=(grid[n-1-i][m-1-j]==1);
                    count+=min(temp,4-temp);
                }
            }
        }
        if(!((extra%4)==2 and f)){
            count+=(extra%4);
        }
        return count;
    }
};
