class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        c = 0
        for i in range(m//2):
            for j in range(n//2):
                ones = grid[i][j] + grid[i][n-1-j] + grid[m-1-i][j] + grid[m-1-i][n-1-j]
                c += min(ones, 4-ones)
        changed = 0
        ones = 0
        if m % 2 == 1:
            for j in range(n//2):
                if grid[m//2][j] != grid[m//2][n-1-j]:
                    changed += 1
                    c += 1
                elif grid[m//2][j] == 1:
                    ones += 1
        if n% 2 == 1:
            for i in range(m//2):
                if grid[i][n//2] != grid[m-1-i][n//2]:
                    changed += 1
                    c += 1   
                elif grid[i][n//2] == 1:
                    ones += 1  
        if m % 2 == 1 and n % 2 == 1:
            c+= grid[m//2][n//2]
        if ones %2 == 1:
            if changed == 0:
                c+= 2

        return c
