#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        int flips = 0;

        // Handle the center element if the grid has odd dimensions
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                flips += 1;
            }
        }

        // Array to count the number of 1s in different scenarios
        vector<int> pairFlips(3, 0);

        // Check rows if the number of rows is odd
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int count = grid[rows / 2][left] + grid[rows / 2][right];
                pairFlips[count] += 1;
                left++;
                right--;
            }
        }

        // Check columns if the number of columns is odd
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int count = grid[top][cols / 2] + grid[bottom][cols / 2];
                pairFlips[count] += 1;
                top++;
                bottom--;
            }
        }

        // Adjust the flips needed based on the counts
        if (pairFlips[2] % 2 == 1) {
            if (pairFlips[1] > 0) {
                flips += pairFlips[1];
            } else {
                flips += 2;
            }
        } else {
            flips += pairFlips[1];
        }

        // Process each quadrant to determine the minimum flips required
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomLeft = grid[rows - 1 - i][j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int total = topLeft + topRight + bottomLeft + bottomRight;

                if (total != 0 && total != 4) {
                    flips += min(total, 4 - total);
                }
            }
        }

        return flips;
    }
};
