class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int cnt=0;
        int n=grid.size(),m=grid[0].size();

        for(int i=0;i<grid.size()/2;i++){
            if(n%2!=0 && i==n/2) continue;
            for(int j=0;j<m/2;j++){
                if(m%2!=0 && j==m/2) continue;
                int ro=grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j];
                cnt+=min(ro,4-ro);
            }


        }

        int ones=0,nm=0;
        if(n%2 && m%2){
            if(grid[n/2][m/2]==1){
                cnt++;
                grid[n/2][m/2]=0;
            }

        }

        // cout<<cnt<<"\n";
        int cnt4=0;
        if(m%2!=0){

            for(int i=0;i<n/2;i++){
               if(grid[i][m/2]==grid[n-1-i][m/2] && grid[n-1-i][m/2]==1) cnt4+=2;
                if(grid[i][m/2]!=grid[n-1-i][m/2]) nm++;
            }
        }
        if(n%2!=0){

            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]==grid[n/2][m-i-1] && grid[n/2][m-i-1]==1) cnt4+=2;
                if(grid[n/2][i]!=grid[n/2][m-i-1]) nm++;
            }
        }
        // cout<<cnt4<<" "<<nm<<"\n";
        cnt4%=4;
        cnt+=nm;
        if((cnt4==2 && nm>=1) || (cnt4==0)){
            return cnt;
        }
        return cnt+2;
    }
};
