class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans=0,ones=0;
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<m;j++){
        //         if(grid[i][j]) ones++;
        //     }
        // }

        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int x = 0;
                x+=grid[i][j];
                x+=grid[i][m-1-j];
                x+=grid[n-1-i][j];
                x+=grid[n-1-i][m-1-j];
                ans += min(x%4, 4 - x%4);
            }
        }
        int x =0;
        if(n%2){
            for(int j=0;j<m/2;j++){
                ones += grid[n/2][j];
                ones += grid[n/2][m-1-j];
                if(grid[n/2][j] != grid[n/2][m-1-j]) {
                    grid[n/2][j] = grid[n/2][m-1-j] = 0;
                    x++;
                    ones--;
                }
            }

        }
        if(m%2){
            for(int i=0;i<n/2;i++){
                ones += grid[i][m/2];
                ones += grid[n-1-i][m/2];

                if(grid[i][m/2] != grid[n-1-i][m/2]) {
                    grid[i][m/2] = grid[n-1-i][m/2] = 0;
                    x++;
                    ones--;
                }
            }
        }

        if(n%2 && m%2){
            ans += grid[n/2][m/2];
        }

        // for(int i=0;i<n;i++){
        //     for(int j=0;j<m;j++){
        //         cout<<grid[i][j]<<" ";
        //     }
        //     cout<<endl;
        // }

        if(x==0) ans += min(ones%4, 4 - ones%4); 

        return ans+x;
    }
};
