class Solution
{
private:
    int solve(vector<vector<int>> &grid)
    {
        int rows = grid.size(), cols = grid[0].size();
        int flips = 0;

        if (rows % 2 == 1 && cols % 2 == 1)
        {
            if (grid[rows / 2][cols / 2] == 1)
            {
                flips += 1;
            }
        }

        vector<int> shiftCount(3, 0);
        if (rows % 2 == 1)
        {
            int left = 0, right = cols - 1;
            while (left < right)
            {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                shiftCount[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        if (cols % 2 == 1)
        {
            int top = 0, bottom = rows - 1;
            while (top < bottom)
            {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                shiftCount[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        if (shiftCount[2] % 2 == 1)
        {
            if (shiftCount[1] > 0)
            {
                flips += shiftCount[1];
            }
            else
            {
                flips += 2;
            }
        }
        else
        {
            flips += shiftCount[1];
        }

        int row = 0;
        while (row < rows / 2)
        {
            int col = 0;
            while (col < cols / 2)
            {
                int topLeft = grid[row][col];
                int bottomLeft = grid[rows - 1 - row][col];
                int topRight = grid[row][cols - 1 - col];
                int bottomRight = grid[rows - 1 - row][cols - 1 - col];

                int sum = topLeft + bottomLeft + topRight + bottomRight;

                if (sum != 0 && sum != 4)
                {
                    flips += min(sum, 4 - sum);
                }
                col += 1;
            }
            row += 1;
        }

        return flips;
    }

public:
    int minFlips(vector<vector<int>> &grid)
    {
        return solve(grid);
    }
};
