class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int extraOnes=0;
        int ops=0;
        for(int i=0;i<n/2;i++){
            int left=i,right=n-i-1;
            for(int j=0;j<m/2;j++){
                int zeroes=0,ones=0;
                if(grid[left][j]==0)zeroes++;
                else ones++;
                if(grid[left][m-j-1]==0)zeroes++;
                else ones++;
                if(grid[right][j]==0)zeroes++;
                else ones++;
                if(grid[right][m-j-1]==0)zeroes++;
                else ones++;            
                if(zeroes>ones)ops+=ones;
                else ops+=zeroes;
            }
        }
        int Ones=0,tempOps=0;
        if(n%2){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]==grid[n/2][m-j-1]){
                    if(grid[n/2][j])Ones+=2;
                    continue;
                }
                tempOps++,ops++;
            }
            if(m%2)extraOnes+=(grid[n/2][m/2]);
        }
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]==grid[n-i-1][m/2]){
                    if(grid[i][m/2])Ones+=2;
                    continue;
            }
                ops++,tempOps++;
            }
        }
        if(extraOnes&&extraOnes%2)ops+=extraOnes;
        if(Ones%4==2&&tempOps==0)ops+=2;
        return ops;
    }
};
