class Solution {
    public int minFlips(int[][] grid) {
        final int numRows = grid.length;
        final int numCols = grid[0].length;
        int count = 0;

        int numEvenPairs = 0;
        int numOddPairs = 0;
        for (int rowIndex = 0; rowIndex < numRows; ++rowIndex) {
            for (int colIndex = 0; colIndex < numCols; ++colIndex) {
                if (rowIndex > numRows - rowIndex - 1 || colIndex > numCols - colIndex - 1) {
                    break;
                } else if (rowIndex == numRows - rowIndex - 1 && colIndex == numCols - colIndex - 1) {
                    count += grid[rowIndex][colIndex];
                } else if (rowIndex == numRows - rowIndex - 1) {
                    if (grid[rowIndex][colIndex] != grid[rowIndex][numCols - colIndex - 1]) {
                        numOddPairs++;
                    } else if (grid[rowIndex][colIndex] == 1) {
                        numEvenPairs++;

                    }
                } else if (colIndex == numCols - colIndex - 1) {
                    if (grid[rowIndex][colIndex] != grid[numRows - rowIndex - 1][colIndex]) {
                        numOddPairs++;
                    } else if (grid[rowIndex][colIndex] == 1) {
                        numEvenPairs++;
                    }
                } else {
                    int numOnes = grid[rowIndex][colIndex];
                    numOnes += grid[rowIndex][numCols - colIndex - 1];
                    numOnes += grid[numRows - rowIndex - 1][colIndex];
                    numOnes += grid[numRows - rowIndex - 1][numCols - colIndex - 1];

                    count += Integer.min(numOnes, 4 - numOnes);
                }
            }
        }

        //System.out.println(count);
        //System.out.println(numOddPairs);
        //System.out.println(numEvenPairs);
        if (numEvenPairs % 2 == 0) {
            count += numOddPairs;
        } else if (numOddPairs > 0) {
            count += numOddPairs;
        } else {
            count += 2;
        }

        return count;
    }
}
