class Solution
{
public:
    int minFlips(vector<vector<int>> &grid)
    {
        int n = grid.size(), m = grid[0].size();
        int res = 0;
        if (n % 2 == 1 && m % 2 == 1 && grid[n / 2][m / 2] == 1) res += 1;

        vector<int> v(3, 0);
        if (n % 2 == 1)
        {
            int i = 0, j = m - 1;
            while (i < j)
            {
                int sm = grid[n / 2][i] + grid[n / 2][j];
                v[sm] += 1;
                i++;
                j--;
            }
        }

        if (m % 2 == 1)
        {
            int i = 0, j = n - 1;
            while (i < j)
            {
                int sm = grid[i][m / 2] + grid[j][m / 2];
                v[sm] += 1;
                i++;
                j--;
            }
        }

        if (v[2] % 2 == 1) res += (v[1] > 0) ? v[1] : 2;
        else res += v[1];

        for (int i = 0; i < n / 2; i++)
            for (int j = 0; j < m / 2; j++){
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];
                int sm = a + b + c + d;

                if (sm != 0 && sm != 4)
                {
                    res += min(sm, 4 - sm);
                }
            }

        return res;
    }
};
