class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for(int i = 0; i < n / 2; i++){
            for(int j = 0; j < m / 2; j++){
                int right = m - j - 1;
                int down = n - i - 1;
                int a = grid[i][right];
                int b = grid[down][j];
                int c = grid[down][right];
                int d = grid[i][j];
                if(a + b + c + d == 1 || a + b + c + d == 2){
                    ans += a + b + c + d;
                }else if(a + b + c + d == 3){
                    ans += 1;
                }
                /*if(i == 0 && j == 0){
                    cout << d << " " << a << " " << b << " " << c << endl;
                }*/
            }
        }
        cout << ans << endl;
        int one1 = 0;
        int diff1 = 0;
        int one2 = 0;
        int diff2 = 0;
        int one3 = 0;
        if(m % 2 == 1){
            int now = m / 2;
            for(int i = 0; i < n / 2; i++){
                if(grid[i][now] != grid[n - i - 1][now]){
                    diff1++;
                }else if(grid[i][now] == 1){
                    one1++;
                }
            }
        }if(n % 2 == 1){
            int now = n / 2;
            for(int i = 0; i < m / 2; i++){
                if(grid[now][i] != grid[now][m - i - 1]){
                    diff2++;
                }else if(grid[now][i] == 1){
                    one2++;
                }
            }
        }
        if(m % 2 == 1 && n % 2 == 1){
            one3 = grid[n / 2][m / 2];
        }
        ans += diff1 + diff2;
        cout << one1 << " " << one2 << endl;
        if(((one1 + one2) * 2) % 4 == 2 && diff1 + diff2 == 0){
            ans += 2;
        }
        if(one3 == 1){
            ans++;
        }
        return ans;
    }
};
