class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        if(n*m==1 && grid[0][0]==1)
        return 1;
        if(n*m==1)
        return 0;
        int ans=0;
        int c=0,z=0;
        for(int i=0;i<(n+1)/2;i++)
        {
            for(int j=0;j<(m+1)/2;j++)
            {
                if(n%2 && m%2 && i==(n-1)/2 && j==(m-1)/2)
                {
                    if(grid[i][j]==1)
                    ans++;
                }
                else if(n%2 && i==(n-1)/2)
                {
                    //   cout<<"2 ";
                      if(grid[i][j]==grid[i][m-1-j])
                      {
                         if(grid[i][j]==1)
                         c++;
                      }
                      else
                      {
                        z++;
                        ans++;
                      }
                }
                else if(m%2 && j==(m-1)/2)
                {
                    if(grid[i][j]==grid[n-1-i][j])
                    {
                         if(grid[i][j]==1)
                         c++;
                    }
                    else
                    {
                        z++;
                        ans++;
                    }
                }
                else
                {   
                    //  cout<<4<<" ";
                     int x1=grid[i][j];
                     int x2=grid[n-1-i][j];
                     int y1=grid[i][m-1-j];
                     int y2=grid[n-1-i][m-1-j];
                    //  cout<<x1<<x2<<y1<<y2;
                     int sum=x1+x2+y1+y2;
                     if(sum%4!=0)
                     {
                         if(sum%2==0)
                         ans+=2;
                         else
                         ans++;
                     }
                }
                // cout<<ans<<" ";
            }
        }
        if(c%2 && z==0)
        ans+=2;
        return ans;
    }
};
