#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<int> reduced4, reduced2;
        for (int i = 0; i < grid.size() / 2; i++) {
            for (int j = 0; j < grid[0].size() / 2; j++) {
                int val = 0;
                val += grid[i][j];
                val += grid[i][grid[0].size() - 1 - j];
                val += grid[grid.size() - 1 - i][j];
                val += grid[grid.size() - 1 - i][grid[0].size() - 1 - j];
                reduced4.push_back(val);
            }
        }
        if (grid.size() % 2 == 1) {
            for (int j = 0; j < grid[0].size() / 2; j++) {
                int val = 0;
                val += grid[grid.size() / 2][j];
                val += grid[grid.size() / 2][grid[0].size() - 1 - j];
                reduced2.push_back(val);
            }
        }
        if (grid[0].size() % 2 == 1) {
            for (int i = 0; i < grid.size() / 2; i++) {
                int val = 0;
                val += grid[i][grid[0].size() / 2];
                val += grid[grid.size() - 1 - i][grid[0].size() / 2];
                reduced2.push_back(val);
            }
        }
        int ans = 0;
        if (grid.size() % 2 == 1 && grid[0].size() % 2 == 1) {
            if (grid[grid.size() / 2][grid[0].size() / 2] == 1) { ans++; }
        }
        int oneCount = 0;
        for (int i = 0; i < reduced4.size(); i++) {
            if (reduced4[i] < 4 - reduced4[i]) {
                ans += reduced4[i];
            }
            else {
                ans += 4 - reduced4[i];
                oneCount += 4;
            }
        }
        for (int i = 0; i < reduced2.size(); i++) {
            if (reduced2[i] == 2) {
                oneCount += 2;
                continue;
            }
        }
        int cnt = 0;
        for (int i = 0; i < reduced2.size(); i++) { cnt += (reduced2[i] == 1); }
        ans += cnt;
        if (oneCount % 4 != 0) {
            //cout << "cnt: " << cnt * 2 << " oneCount: " << oneCount % 4 << endl;
            if (cnt * 2 >= oneCount % 4) { oneCount = 0; }
        }
        if (oneCount % 4 != 0) { ans += oneCount % 4; cout << "WTF" << endl; }
        return ans;
    }
};
