class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<vector<int>> matrix = grid;
        int rowCount = matrix.size();
        int colCount = matrix[0].size();
        int totalFlips = 0;
        for(int r = 0; r<rowCount / 2; r++){
            for(int c = 0; c<colCount / 2; c++){
                vector<int> subGridValues = {
                    matrix[r][c],
                    matrix[rowCount - r - 1][c],
                    matrix[r][colCount - c - 1],
                    matrix[rowCount - r - 1][colCount - c - 1]
                };

                int countOnes = accumulate(subGridValues.begin(), subGridValues.end(), 0);

                totalFlips += min(countOnes, 4 - countOnes);
            }
        }
        int singleRowFlips = 0;
        int middleOnes = 0;
        if(rowCount % 2 != 0){
            int middleRow = rowCount / 2;
            for(int c = 0; c < colCount / 2; ++c){
                if (matrix[middleRow][c] != matrix[middleRow][colCount - c - 1]) {
                    totalFlips += 1;
                    singleRowFlips += 1;
                }else{
                    if(matrix[middleRow][c] == 1){
                        middleOnes += 1;
                    }
                }
            }
        }
        if(colCount % 2 != 0){
            int middleCol = colCount / 2;
            for(int r = 0; r < rowCount / 2; ++r){
                if(matrix[r][middleCol] != matrix[rowCount - r - 1][middleCol]) {
                    totalFlips += 1;
                    singleRowFlips += 1;
                }else{
                    if(matrix[r][middleCol] == 1){
                        middleOnes += 1;
                    }
                }
            }
        }
        if(rowCount % 2 != 0 && colCount % 2 != 0 && matrix[rowCount / 2][colCount / 2] == 1){
            totalFlips += 1;
        }
        if(singleRowFlips == 0 && middleOnes % 2 != 0){
            totalFlips += 2;
        }
        return totalFlips;
    }
};
