class Solution {
public:
  int minFlips(vector<vector<int>>& grid) {
    int n = grid.size(), m = grid[0].size(), ans = 0;

    for(int i = 0; i < n/2; i++) {

      for(int j = 0; j < m/2; j++) {

        vector<int> vec(2);

        vec[grid[i][j]]++, vec[grid[n - i - 1][j]]++, vec[grid[i][m - j - 1]]++, vec[grid[n - i - 1][m - j - 1]]++;

        ans += min(vec[0], vec[1]);

      }

    }

    int ones = 0, x1 = 0, x2 = 0;

    if(m & 1) {

      for(int i = 0; i < n/2; i++) {

        ones += grid[i][m/2] + grid[n - i - 1][m/2];

        x1 += (grid[i][m/2] != grid[n - i - 1][m/2]), ans += (grid[i][m/2] != grid[n - i - 1][m/2]), ones += (grid[i][m/2] != grid[n - i - 1][m/2]);

      }

    }

    if(n & 1) {

      for(int j = 0; j < m/2; j++) {

        ones += grid[n/2][j] + grid[n/2][m - j - 1];

        x2 += (grid[n/2][j] != grid[n/2][m - j - 1]), ans += (grid[n/2][j] != grid[n/2][m - j - 1]), ones += (grid[n/2][j] != grid[n/2][m - j - 1]);

      }

    }


    if((n & 1) && (m & 1) && grid[n/2][m/2]) {
      ones++;
    }

    if(ones % 4 == 1) ans++;
    else if(ones % 4 == 2) {

      if(x1 || x2) {}
      else ans += 2;

    }else if(ones % 4 == 3) {
      if(x1 || x2) {}
      else ans += 2;
      ans++;
    }

    return ans;
  }
};
