function minFlips(grid: number[][]): number {
  const row = grid.length;
  const col = grid[0].length;
  let ans = 0;
  let roo = 0;
  let odd = 0;
  let oneCouple = 0;
  for (let i = 0; i < row / 2; i++) {
    for (let j = 0; j < col / 2; j++) {
      const ktt = [grid[i][j], grid[row - i - 1][j], grid[i][col - j - 1], grid[row - i - 1][col - j - 1]];
      let one = ktt.filter(num => num).length;
      let tag = 0;
      if (i === row - i - 1) {
        one /= 2;
        tag++;
      }
      if (j === col - j - 1) {
        one /= 2;
        tag++;
      }
      if (tag === 2) {
        roo = one;
      } else if (tag === 1) {
        if (one === 1) {
          ans++;
          odd++;
        } else if(one === 2) {
          oneCouple++;
        }
      } else {
        ans += Math.min(one, 4 - one);
      }
    }
  }
  if (roo) {
    ans++;
  }
  if (oneCouple % 2) {
    if (!odd) {
      ans += 2;
    }
  }
  return ans;
};
