class Solution {
    public int minFlips(int[][] grid) {
        int r = grid[0].length;
        int b = grid.length;
        int cnt = 0;
        for(int i = 0; i < grid.length/2; i++){
            for(int j = 0; j < grid[0].length/2; j++){
                int zero = 0;
                int one = 0;
                if(grid[i][j]==0) zero++;
                else one++;
                if(grid[i][r-j-1]==0) zero++;
                else one++;
                if(grid[b-i-1][j]==0) zero++;
                else one++;
                if(grid[b-i-1][r-j-1]==0) zero++;
                else one++;
                cnt += Math.min(zero, one);

            }
        }
        int l = 0;
        int h = grid[0].length-1;
        int ones = 0;
        int mis = 0;
        if(b%2==1)
            while(l<h){
                if(grid[b/2][l]!=grid[b/2][h]) mis++;
                else{
                    if(grid[b/2][l]==1) ones+=2;
                }
                l++;
                h--;
            }
        l = 0;
        h = grid.length-1;
        if(r%2==1)
            while(l<h){
                if(grid[l][r/2]!=grid[h][r/2]) mis++;
                else{
                    if(grid[l][r/2]==1) ones+=2;
                }
                l++;
                h--;
            }
        int req = Math.min(ones%4, 4-ones%4);
        // cnt += req/2;
        req /= 2;
        if(mis<req) {
            req -= mis;
            cnt+=2*req;
        }else{
            cnt += mis;
        }
        if(b%2==1 && r%2==1 && grid[b/2][r/2]==1) cnt++;
        return cnt;
    }
}
