class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = INT_MAX;
        int n = grid.size();
        int m = grid[0].size();
        int cur = 0;
        int count1 = 0, count0 =0;
        // for(int i=0; i<n; i++) {
        //     for(int j=0; j<m; j++) {
        //         if(grid[i][j]) countOne++;
        //     }
        // }
        for(int i=0; i<(n)/2; i++) {
            for(int j=0; j<(m)/2; j++) {
                int temp = 0;
                temp += grid[i][j] + grid[i][m-j-1] + grid[n-i-1][j] + grid[n-i-1][m-j-1];
                // cout<<i<<" "<<j<<" "<<temp<<"\n";
                if(temp == 1 or temp == 3) cur++;
                else if(temp == 2) cur += 2;
                grid[i][j] = grid[i][m-j-1] = grid[n-i-1][j] = grid[n-i-1][m-j-1] = 0;
            }
        }
        int cur2 =0;
        if(n%2) {
            for(int i=0; i<m/2; i++) {
                if(grid[n/2][i] != grid[n/2][m-i-1]) cur2++;

                if(grid[n/2][i]) count1++;
                else count0++;

                if(grid[n/2][m-i-1]) count1++;
                else count0++;
            }
        }
        if(m%2) {
            for(int i=0; i<n/2; i++) {
                if(grid[i][m/2] != grid[n-i-1][m/2]) cur2++;

                if(grid[i][m/2]) count1++;
                else count0++;

                if(grid[n-i-1][m/2]) count1++;
                else count0++;
            }
        }
        if(n%2 and m%2 and grid[n/2][m/2]) count1++;
        int rem = count1%4;
        // cout<<cur<<" "<<rem<<" "<<cur2<<"\n";
        if(cur2 >= 1) {
            if(cur2%2 == rem%2) return cur+cur2;
            return cur+cur2+1;
        }
        return cur + cur2 + rem;

    }
};
