class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        # 1为4的倍数
        # grid[i][j] = grid[row-1-i][j] = grid[i][col-1-j] = grid[row-1-i][col-1-j]
        row, col = len(grid), len(grid[0])

        if row == 1:
            if col <= 3:
                return grid[0].count(1)
            if col == 4:
                return min(grid[0].count(1), 4 - grid[0].count(1))

        if col == 1:
            temp = [grid[i][0] for i in range(row)].count(1)
            if row <= 3:
                return temp
            if row == 4:
                return min(temp, 4 - temp)



        ans = 0
        for i in range(row // 2):
            for j in range(col // 2):
                lst = [grid[i][j], grid[row-1-i][j], grid[i][col-1-j], grid[row-1-i][col-1-j]]
                # cur1 = lst.count(1)
                # cur0 = lst.count(0)
                ans += min(lst.count(0), lst.count(1))



        if row % 2 and col % 2:
            if grid[row // 2][col // 2] == 1:
                ans += 1

        ones = 0
        flag = False
        if row % 2:
            mid = row // 2
            left, right = 0, col - 1
            while left < right:
                if grid[mid][left] != grid[mid][right]:
                    ans += 1
                    flag = True
                if grid[mid][left] == grid[mid][right] == 1:
                    ones += 2
                left += 1
                right -= 1


        if col % 2:
            mid = col // 2
            left, right = 0, row - 1
            while left < right:
                if grid[left][mid] != grid[right][mid]:
                    ans += 1
                    flag = True
                if grid[left][mid] == grid[right][mid] == 1:
                    ones += 2
                left += 1
                right -= 1
        # return ans
        return ans + 2 if (ones % 4 == 2 and not flag) else ans
        # return ans if ones != 2 else ans + 2
