class Solution {
public:

    int minFlips(vector<vector<int>>& grid) {
         int n=grid.size();
        int m=grid[0].size();
        int colstart=0,colend=m-1;
        int ans=0;
        int extra=0;
         int a=0,b=n-1;
        while(colstart<colend)
        {
                a=0,b=n-1;

                 while(a<b)
                 {
                     int cnt=0;
                     cnt+=grid[a][colstart];
                     cnt+=grid[a][colend];
                     cnt+=grid[b][colstart];
                     cnt+=grid[b][colend];
                     ans+=min(cnt,4-cnt);
                     a++;
                     b--;
                 }
            colstart++;
            colend--;


        }
        // cout<<ans<<" ";
        int cnt=0,flip=0;
        if(colstart==colend)
        {

            int s=0,e=n-1;
            while(s<e)
            {
                cnt+=grid[s][colstart];
                cnt+=grid[e][colstart];
                if(grid[s][colstart]!=grid[e][colstart])
                {flip++;
                }
                s++;
                e--;
            }


        }
        if(a==b)
        {
            int s=0,e=m-1;
            while(s<e)
            {
                cnt+=grid[a][s];
                cnt+=grid[a][e];
                if(grid[a][s]!=grid[a][e])
                {flip++;
                    }
                s++;
                e--;
            }
        }
        if(cnt%4!=0) {
            if(flip==0) {
                ans+=2;
            }
        }
        if(n%2 && m%2) {
            ans+=(grid[n/2][m/2]);
        }

        return ans+flip;








    }
};
