/**
 * @param {number[][]} grid
 * @return {number}
 */


var minFlips = function(grid) {
    const n = grid.length;
    const m = grid[0].length;
    let ans = 0;
    //check for odd
    if (n % 2 === 1 && m % 2 === 1) {
        if (grid[Math.floor(n / 2)][Math.floor(m / 2)] === 1) {
            ans += 1;
        }
    }

    const shift = [0, 0, 0];
    if (n % 2 === 1) {
        let a = 0, b = m - 1;
        while (a < b) {
            const sm = grid[Math.floor(n / 2)][a] + grid[Math.floor(n / 2)][b];
            shift[sm] += 1;
            a += 1;
            b -= 1;
        }
    }

    if (m % 2 === 1) {
        let i = 0;
        let j = n - 1;
        while (i < j) {
            const sum = grid[i][Math.floor(m / 2)] + grid[j][Math.floor(m / 2)];
            shift[sum] += 1;
            i += 1;
            j -= 1;
        }
    }

    if (shift[2] % 2 === 1) {
        if (shift[1] > 0) {
            ans += shift[1];
        } else {
            ans += 2;
        }
    } else {
        ans += shift[1];
    }

    for (let i = 0; i < Math.floor(n / 2); ++i) {
        for (let j = 0; j < Math.floor(m / 2); ++j) {
            const x = grid[i][j];
            const y = grid[n - 1 - i][j];
            const z = grid[i][m - 1 - j];
            const q = grid[n - 1 - i][m - 1 - j];

            const sum = x + y + z + q;

            if (sum !== 0 && sum !== 4) {
                ans += Math.min(sum, 4 - sum);
            }
        }
    }

    return ans;
};
