class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        def f(num):
            if num % 2 == 0:
                return num // 2
            else:
                return num // 2 + 1
        res = 0
        dic = Counter()
        for i in range(f(m)):
            for j in range(f(n)):
                x1, y1 = i, j
                x2, y2 = i, n - 1 - j
                x3, y3 = m - 1 - i, j
                x4, y4 = m - 1 - i, n - 1 - j
                L = [(x1, y1), (x2, y2), (x3, y3), (x4, y4)]
                # print(L)
                if y1 != y2 and x1 != x3:
                    a = 0
                    b = 0
                    for x, y in L:
                        if grid[x][y] == 0:
                            a += 1
                        else:
                            b += 1
                    res += min(a, b)
                else:
                    a = 0
                    b = 0
                    for x, y in set(L):
                        if grid[x][y] == 0:
                            a += 1
                        else:
                            b += 1
                    if len(set(L)) == 2:
                        dic[b] += 1
                    else:
                        res += b
                # print(res)
        yu1 = dic[2] % 2
        # print(dic)
        if yu1 == 0:
            res += dic[1]
        else:
            if dic[1] == 0:
                res += 2
            else:
                res += dic[1]

        return res
